"use strict";var gulp=require("gulp"),jshint=require("gulp-jshint"),babel=require("gulp-babel"),uglify=require("gulp-uglify");gulp.task("processHTML",function(){gulp.src("*.html").pipe(gulp.dest("dist"))}),gulp.task("processJS",function(){gulp.src("*.js").pipe(jshint({esversion:8})).pipe(jshint.reporter("default")).pipe(babel({presets:["@babel/preset-env"]})).pipe(uglify()).pipe(gulp.dest("dist"))}),gulp.task("babelPolyfill",function(){gulp.src("node_modules/babel-polyfill/browser.js").pipe(gulp.dest("dist/node_modules/babel-polyfill"))}),gulp.task("processImages",function(){gulp.src("./images/*.svg").pipe(gulp.dest("dist/images"))});